document.addEventListener("DOMContentLoaded",function(){let isStickyPriceClosed=false;document.querySelectorAll(".gm-button-close").forEach(closeButton=>{closeButton.addEventListener("click",event=>{const stickyPrice=closeButton.closest(".sticky_price");if(stickyPrice){stickyPrice.style.display="none";isStickyPriceClosed=true;event.stopPropagation()}})});const stickyPrice=document.querySelector(".sticky_price");if(stickyPrice){stickyPrice.addEventListener("click",event=>{if(!event.target.closest(".gm-button")&&!event.target.closest(".gm-button-close")){const targetLink=stickyPrice.querySelector(".gm-button");if(targetLink){targetLink.click()}}})}window.addEventListener("scroll",()=>{if(isStickyPriceClosed)return;if(stickyPrice){const parentUl=stickyPrice.closest("ul");if(parentUl){const ulBottomFromPageTop=parentUl.offsetTop+parentUl.offsetHeight;const scrollPosition=window.scrollY+window.innerHeight;if(scrollPosition>=ulBottomFromPageTop){stickyPrice.style.display="flex"}else{stickyPrice.style.display="none"}}}})});